from tkinter import *

e=6553

cipher=[]
def Encrypt():
	plaintext=stv.get()
	for x in plaintext:
		cipher.append(pow(ord(x),e,n))
	stv.set(cipher)
	print("Encrypted")

def Decrypt():
	global cipher
	decryption=[]
	plaintextset=[]
	for y in cipher:
		decryption.append(pow(y,d,n))
	for char in decryption:
		plaintextset.append(chr(char))
	plaintext="".join(plaintextset)
	print("Decrypted")
	stv.set(plaintext)
	cipher=[]

root = Tk()
file1 = open("Interface.txt","a+") 
stv = StringVar()
entry = Entry(textvariable=stv)
btn = Button(text="Click to encrypt data",command=Encrypt)
btn1 = Button(text="Click to decrypt data",command=Decrypt)
label = Label(text="Please Enter Data")
label.pack()
entry.pack(side=LEFT)
btn.pack()
btn1.pack()
root.mainloop()